mapper.select:
select

from data_stream

    is_deleted = 0

        and business_identifier = #{bid, jdbcType=VARCHAR}


        and data_stream_identifier = #{dsid, jdbcType=VARCHAR}

select count(1)
from data_stream
where business_identifier = #{bid, jdbcType=VARCHAR}
  and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
  and is_deleted = 0

select
from data_stream
is_deleted = 0 and (creator = #{currentUser,jdbcType=VARCHAR} or find_in_set(#{currentUser,jdbcType=VARCHAR}, in_charges))
and business_identifier = #{bid, jdbcType=VARCHAR}
and data_source_type = #{dataSourceType, jdbcType=VARCHAR}
and (name like CONCAT('%', #{keyWord}, '%') or description like CONCAT('%', #{keyWord}, '%'))
and status = #{status, jdbcType=INTEGER}
order by modify_time desc
