mapper.select:
select

from source_db_detail

    is_deleted = 0

        and business_identifier = #{bid, jdbcType=VARCHAR}


        and (transfer_ip like CONCAT('%', #{keyWord}, '%') or connection_name like CONCAT('%', #{keyWord}, '%'))


        and access_type = #{accessType, jdbcType=VARCHAR}


        and status = #{status, jdbcType=INTEGER}


order by modify_time desc

select count(1)
from source_db_detail
where business_identifier = #{businessIdentifier, jdbcType=VARCHAR}
  and data_stream_identifier = #{dataStreamIdentifier, jdbcType=VARCHAR}
  and db_name = #{dbName, jdbcType=VARCHAR}
  and connection_name = #{connectionName, jdbcType=VARCHAR}
  and is_deleted = 0

select

from source_db_detail

    is_deleted = 0

        and business_identifier = #{bid, jdbcType=VARCHAR}


        and data_stream_identifier = #{dsid, jdbcType=VARCHAR}


order by modify_time desc

mapper.delete:
delete
from source_db_detail
where business_identifier = #{bid, jdbcType=VARCHAR}
  and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
  and is_deleted = 0

mapper.update:
update source_db_detail
set is_deleted = 1,
    modifier   = #{operator,jdbcType=VARCHAR}
where business_identifier = #{bid, jdbcType=VARCHAR}
  and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
  and is_deleted = 0
